export function Pokemon({ pokemon, handleSubmit }) {
  const formatName = (name) => {
    return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="pokemon">üîç Search for a Pok√©mon:</label>
        <br />
        <input
          id="pokemon"
          name="pokemon"
          placeholder="Type part of the name here..."
        />
        <button type="submit">Search</button>
      </form>
      <div className="pokemon-container">
        <ul className="pokemon-list">
          {pokemon.map((poke, index) => (
            <li className="card" key={index}>
              <div className="pokemon-info">
                <div className="pokemon-text">
                  <p className="pokemon-info-number">
                    #{poke.id} {formatName(poke.name)}
                  </p>
                  <p className="pokemon-info-name">{formatName(poke.name)}</p>
                  {poke.types && poke.types.length > 0 && (
                    <div className="pokemon-types">
                      {poke.types.map((typeObj, typeIndex) => (
                        <p key={typeIndex} className="pokemon-type">
                          {typeObj.type.name} {/* Accessing the type name */}
                        </p>
                      ))}
                    </div>
                  )}
                </div>
                {poke.sprites?.front_default && (
                  <img
                    className="pokemon-img"
                    src={poke.sprites.front_default}
                    alt={poke.name}
                  />
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
