export function DetailedPokemon({ detailedPokemon, handleSubmit }) {
  const formatName = (name) => {
    return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  };
  return (
    <div className="pokemon-detailed">
      <div className="pokemon-detailed-img">
        <img
          src={detailedPokemon.sprites?.front_default}
          alt={detailedPokemon.name}
        />
      </div>
      <div className="pokemon-detailed-info">
        <h2>{formatName(detailedPokemon.name)}</h2>
        <p>
          <strong>Height:</strong> {detailedPokemon.height} decimeters
        </p>
        <p>
          <strong>Weight:</strong> {detailedPokemon.weight} hectograms
        </p>
        <p>
          <strong>Abilities:</strong>{" "}
          {detailedPokemon.abilities.map((ability, index) => (
            <span key={index}>{ability.ability.name}</span>
          ))}
        </p>
      </div>
    </div>
  );
}
