import { useState } from "react";
function App() {
  const [pokemon, setPokemon] = useState([]);
  const [pokemonName, setPokemonName] = useState("");

  // Function to fetch a single Pokemon by name
  async function getPokemonByName(name) {
    try {
      const res = await fetch(
        `https://pokeapi.co/api/v2/pokemon/${name.toLowerCase()}`
      );
      if (!res.ok) {
        throw new Error("Pokémon not found");
      }
      const data = await res.json();
      setPokemon(data); // Set the detailed Pokémon data
    } catch (error) {
      alert(error.message);
      setPokemon(null);
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    if (pokemonName) {
      getPokemonByName(pokemonName);
      setPokemonName("");
    }
  }

  async function getPokemon() {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon?limit=10"); // Limit the number of Pokemon for demo purposes
    const data = await res.json();

    // Fetch detailed info for each Pokemon, as the initial request doesn't include sprites and id
    const detailedPokemonPromises = data.results.map(async (poke) => {
      const res = await fetch(poke.url); // Fetch each Pokemon's detailed data using its URL
      return res.json(); // Return detailed Pokemon data
    });

    const detailedPokemon = await Promise.all(detailedPokemonPromises); // Wait for all requests to complete
    setPokemon(detailedPokemon); // Set detailed Pokemon data, including id and sprites
  }

  return (
    <div className="App">
      <h1>Pokémon List</h1>
      <Pokemon pokemon={pokemon} getPokemon={getPokemon} />
      <PokemonName pokemon={pokemon} searchPokemon={handleSubmit}></PokemonName>
    </div>
  );
}

function Pokemon({ pokemon, getPokemon }) {
  return (
    <div>
      <button onClick={getPokemon}>Get Pokémon</button>
      <ul>
        {pokemon.map((poke, index) => (
          <li key={index}>
            <p>{poke.id}</p>
            <p>{poke.name}</p>
            {poke.sprites?.front_default && (
              <img src={poke.sprites.front_default} alt={poke.name} />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

function PokemonName({ pokemon, handleSubmit }) {
  return (
    <div>
      <form>
        <label for="pokemon">🔍Search for a Pokemon:</label>
        <br />
        <input
          id="pokemon"
          name="pokemon"
          placeholder="type here..."
          onChange={handleSubmit}
        ></input>
        <ul>
          {pokemon.map((poke, index) => (
            <li key={index}>
              <p>{poke.id}</p>
              <p>{poke.name}</p>
              {poke.sprites?.front_default && (
                <img src={poke.sprites.front_default} alt={poke.name} />
              )}
            </li>
          ))}
        </ul>
      </form>
    </div>
  );
}

export default App;
