import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
export function Pokemon({ pokemon, handleSubmit }) {
  const [detailedPokemon, setPokemon] = useState([]);

  function getPokemonCard(event) {
    setPokemon(event.target.value);
    console.log(event.target.value);
  }

  // State to store all Pokémon for filtering

  const formatName = (name) => {
    return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  };

  return (
    <div className="search-container">
      <form className="form" onSubmit={handleSubmit}>
        <div className="search-bar">
          <div className="search-box">
            <button type="submit" className="btn-search">
              <FontAwesomeIcon icon={faMagnifyingGlass}></FontAwesomeIcon>
            </button>
            <input
              type="text"
              class="input-search"
              placeholder="Search for a pokemon..."
              id="pokemon"
              name="pokemon"
            />
          </div>
        </div>
      </form>
      <div className="pokemon-container">
        <ul className="pokemon-list">
          {pokemon.map((poke, index) => (
            <li
              onClick={getPokemonCard}
              className="card"
              key={index}
              value={poke}
            >
              <div className="pokemon-info">
                <div className="pokemon-text">
                  <p className="pokemon-info-number-name">
                    <span style={{ fontWeight: "bold" }}>#{poke.id}</span>{" "}
                    {formatName(poke.name)}
                  </p>
                  {poke.types && poke.types.length > 0 && (
                    <div className="pokemon-types">
                      {poke.types.map((typeObj, typeIndex) => (
                        <p
                          key={typeIndex}
                          className={`pokemon-type ${typeObj.type.name}`}
                        >
                          {typeObj.type.name} {/* Accessing the type name */}
                        </p>
                      ))}
                    </div>
                  )}
                </div>
                {poke.sprites?.front_default && (
                  <img
                    className="pokemon-img"
                    src={poke.sprites.front_default}
                    alt={poke.name}
                  />
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export function detailedPokemon(detailedPokemon) {
  const formatName = (name) => {
    return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  };
  return (
    <div className="pokemon-detailed">
      <div className="pokemon-detailed-img">
        <img
          src={detailedPokemon.sprites?.front_default}
          alt={detailedPokemon.name}
        />
      </div>
      <div className="pokemon-detailed-info">
        <h2>{formatName(detailedPokemon.name)}</h2>
        <p>
          <strong>Height:</strong> {detailedPokemon.height} decimeters
        </p>
        <p>
          <strong>Weight:</strong> {detailedPokemon.weight} hectograms
        </p>
        <p>
          <strong>Abilities:</strong>{" "}
          {detailedPokemon.abilities.map((ability, index) => (
            <span key={index}>{ability.ability.name}</span>
          ))}
        </p>
      </div>
    </div>
  );
}
