export function Pokemon({ pokemon, handleSubmit }) {
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="pokemon">üîç Search for a Pok√©mon:</label>
        <br />
        <input
          id="pokemon"
          name="pokemon"
          placeholder="Type part of the name here..."
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {pokemon.map((poke, index) => (
          <li key={index}>
            <p>#{poke.id}</p>
            <p>{poke.name}</p>
            {poke.sprites?.front_default && (
              <img src={poke.sprites.front_default} alt={poke.name} />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
