import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";

export function DetailedPokemon({
  detailedPokemon,
  handleSubmit,
  searchQuery,
  handleSearchChange,
}) {
  const formatName = (name) => {
    return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  };

  return (
    <>
      {/* Check if detailedPokemon is present */}
      {detailedPokemon ? (
        <>
          <form className="form" onSubmit={handleSubmit}>
            <div className="search-bar">
              <div className="search-box">
                <button type="submit" className="btn-search">
                  <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
                <input
                  type="text"
                  className="input-search"
                  placeholder="Search for a Pokémon..."
                  id="pokemon"
                  name="pokemon"
                  value={searchQuery} // Bind input value to searchQuery
                  onChange={handleSearchChange} // Handle input changes
                />
              </div>
            </div>
          </form>

          <div className="pokemon-detailed">
            <div className="pokemon-detailed-img">
              <img
                src={detailedPokemon.sprites?.front_default}
                alt={detailedPokemon.name}
              />
            </div>
            <div className="pokemon-detailed-info">
              <h2>{formatName(detailedPokemon.name)}</h2>
              <p>
                <strong>Height:</strong> {detailedPokemon.height} decimeters
              </p>
              <p>
                <strong>Weight:</strong> {detailedPokemon.weight} hectograms
              </p>
              <p>
                <strong>Abilities:</strong>{" "}
                {detailedPokemon.abilities.map((ability, index) => (
                  <span key={index}>{ability.ability.name}</span>
                ))}
              </p>
            </div>
          </div>
        </>
      ) : (
        <p>No Pokémon selected. Please search for a Pokémon.</p> // Message when no Pokémon is selected
      )}
    </>
  );
}
