import { useState, useEffect } from "react";

function App() {
  const [allPokemon, setAllPokemon] = useState([]); // State to store all Pok√©mon for filtering
  const [filteredPokemon, setFilteredPokemon] = useState([]); // State to store filtered Pok√©mon based on search

  // Function to fetch a large list of Pok√©mon
  async function getAllPokemon() {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon?limit=500"); // Fetch a large batch of Pok√©mon
    const data = await res.json();
    setAllPokemon(data.results); // Store basic Pok√©mon info (name and URL)
    setFilteredPokemon(data.results); // Initially show all Pok√©mon
  }

  // Function to fetch detailed Pok√©mon data for the filtered results
  async function getDetailedPokemon(filteredList) {
    const detailedPokemonPromises = filteredList.map(async (poke) => {
      const res = await fetch(poke.url); // Fetch each Pok√©mon's detailed data using its URL
      return res.json(); // Return detailed Pok√©mon data
    });

    const detailedPokemon = await Promise.all(detailedPokemonPromises); // Wait for all requests to complete
    setFilteredPokemon(detailedPokemon); // Set detailed Pok√©mon data
  }

  // Handle form submission and filtering
  function handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData(event.target); // Get form data
    const searchTerm = formData.get("pokemon").toLowerCase(); // Get the search term and make it lowercase for comparison

    if (searchTerm) {
      // Filter Pok√©mon whose name contains the search term
      const filteredList = allPokemon.filter((poke) =>
        poke.name.toLowerCase().includes(searchTerm)
      );
      // Fetch detailed data for the filtered Pok√©mon
      getDetailedPokemon(filteredList);
    } else {
      // If no search term, show all Pok√©mon
      getDetailedPokemon(allPokemon);
    }
  }

  useEffect(() => {
    getAllPokemon(); // Fetch the list of all Pok√©mon when the component mounts
  }, []);

  return (
    <div className="App">
      <h1>Pok√©mon List</h1>
      <Pokemon pokemon={filteredPokemon} handleSubmit={handleSubmit} />
    </div>
  );
}

function Pokemon({ pokemon, handleSubmit }) {
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="pokemon">üîç Search for a Pok√©mon:</label>
        <br />
        <input
          id="pokemon"
          name="pokemon"
          placeholder="Type part of the name here..."
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {pokemon.map((poke, index) => (
          <li key={index}>
            <p>#{poke.id}</p>
            <p>{poke.name}</p>
            {poke.sprites?.front_default && (
              <img src={poke.sprites.front_default} alt={poke.name} />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
